<template>
  <div class="promise_box">
    <div ref="imageTofile" class="promise">
      <img @click="cilc" class="backgroundImg" :src="CHENGNUO" alt="">
      <img id="newImg" class="name" :src="base64" alt="">
      <div class="email_box">{{userEmail}}</div>
      <div class="time_box">{{systemDate}}</div>
      <div class="department_box">{{department}}</div>
      <div class="jobNum_box">{{jobNum}}</div>
    </div>
  </div>
</template>

<script>
import CHENGNUO from '@/assets/chengnuoshu.jpg'
import html2canvas from 'html2canvas'
import http from '@/api/http'
import { Api } from '@/api/api'
export default {
  name: 'promise',
  props: {
    base64: {
      type: String,
      default: ''
    }
  },
  data () {
    return {
      jobNum: localStorage.getItem('astlcode') === 'undefined' ? '' : localStorage.getItem('astlcode'),
      systemDate: '',
      department: localStorage.getItem('department') === 'undefined' ? '' : localStorage.getItem('department'),
      userEmail: localStorage.getItem('userEmail'),
      htmlUrl: '',
      global: {
      },
      CHENGNUO: CHENGNUO,
      baseImg: 'data:image/png;base64,'
    }
  },
  watch: {
    base64 () {
      setTimeout(() => {
        this.toImage()
      }, 1000)
    }
  },
  created: function () {
    this.addDate()
  },
  mounted () {
  },
  methods: {
    toImage () {
      // 第一个参数是需要生成截图的元素,第二个是自己需要配置的参数,宽高等
      html2canvas(this.$refs.imageTofile, {
        backgroundColor: null
      }).then((canvas) => {
        let url = canvas.toDataURL('image/png')
        this.htmlUrl = url

        var blob = this.getBlob(canvas)
        var oMyForm = new FormData()
        var fileName = 'mobile' + '.jpg'
        oMyForm.append('file', blob, fileName)
        oMyForm.append('fileType', 'image')
        http.post(Api.uploadFile, oMyForm).then(res => {
          let params = {
            type: 6,
            name: localStorage.getItem('userName'),
            userid: localStorage.getItem('userId'),
            image: res.data
          }
          http.post(Api.saveActivity, params).then(res => {
            this.$emit('base64succeed', res)
          })
        })
      })
    },
    getBlob (canvas) { // 获取blob对象
      var data = canvas.toDataURL('image/jpeg', 1)
      data = data.split(',')[1]
      data = window.atob(data)
      var ia = new Uint8Array(data.length)
      for (var i = 0; i < data.length; i++) {
        ia[i] = data.charCodeAt(i)
      }
      return new Blob([ia], {
        type: 'image/jpeg'
      })
    },
    addDate () {
      let nowDate = new Date()
      let date = {
        year: nowDate.getFullYear(),
        month: nowDate.getMonth() + 1,
        date: nowDate.getDate()
      }
      this.systemDate = date.year + '.' + 0 + date.month + '.' + 0 + date.date
    },
    cilc () {
      this.toImage()
    }
  },
  components: {}
}
</script>
